package com.coinninja.coinkeeper.db.schema;

import org.greenrobot.greendao.database.Database;

public class V27_Schema {
    public void create(Database db) {
        db.execSQL("CREATE TABLE IF NOT EXISTS \"EXTERNAL_NOTIFICATION\" (\"_id\" INTEGER PRIMARY KEY AUTOINCREMENT ,\"MESSAGE\" TEXT UNIQUE ,\"EXTRA_DATA\" TEXT)");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"INVITE_TRANSACTION_SUMMARY\" (\"WALLET_ID\" INTEGER,\"TRANSACTIONS_INVITES_SUMMARY_ID\" INTEGER,\"TYPE\" INTEGER,\"BTC_STATE\" INTEGER,\"_id\" INTEGER PRIMARY KEY AUTOINCREMENT ,\"SERVER_ID\" TEXT UNIQUE ,\"INVITE_NAME\" TEXT,\"BTC_TRANSACTION_ID\" TEXT,\"SENT_DATE\" INTEGER,\"SENDER_PHONE_NUMBER\" TEXT,\"RECEIVER_PHONE_NUMBER\" TEXT,\"ADDRESS\" TEXT,\"PUBKEY\" TEXT,\"VALUE_SATOSHIS\" INTEGER,\"VALUE_FEES_SATOSHIS\" INTEGER,\"HISTORIC_VALUE\" INTEGER NOT NULL ,\"HISTORIC_USDVALUE\" TEXT,\"TRANSACTION_NOTIFICATION_ID\" INTEGER)");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"CONTACT\" (\"_id\" INTEGER PRIMARY KEY AUTOINCREMENT ,\"PHONE_NUMBER_HASH\" TEXT)");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"FUNDING_STAT\" (\"_id\" INTEGER PRIMARY KEY ,\"ADDRESS_ID\" INTEGER,\"TSID\" INTEGER,\"WALLET_ID\" INTEGER,\"ADDR\" TEXT,\"POSITION\" INTEGER NOT NULL ,\"VALUE\" INTEGER NOT NULL ,\"TARGET_ID\" INTEGER,\"FUNDED_TRANSACTION\" TEXT,\"STATE\" INTEGER)");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"BROADCAST_BTC_INVITE\" (\"_id\" INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE ,\"BROADCAST_TX_ID\" TEXT UNIQUE ,\"BTC_STATE\" INTEGER,\"BROADCAST_TO_ADDRESS\" TEXT,\"INVITE_SERVER_ID\" TEXT UNIQUE ,\"INVITE_TRANSACTION_SUMMARY_ID\" INTEGER)");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"WORD\" (\"_id\" INTEGER PRIMARY KEY AUTOINCREMENT ,\"WALLET_ID\" INTEGER,\"WORD\" TEXT,\"SORT_ORDER\" INTEGER NOT NULL )");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"USER\" (\"_id\" INTEGER PRIMARY KEY AUTOINCREMENT ,\"PIN\" TEXT,\"UID\" TEXT,\"COMPLETED_TRAINING\" INTEGER NOT NULL ,\"LOCKED_UNTIL_TIME\" INTEGER NOT NULL )");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"ADDRESS\" (\"_id\" INTEGER PRIMARY KEY AUTOINCREMENT ,\"ADDRESS\" TEXT UNIQUE ,\"WALLET_ID\" INTEGER,\"INDEX\" INTEGER NOT NULL ,\"CHANGE_INDEX\" INTEGER NOT NULL )");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"ACCOUNT\" (\"_id\" INTEGER PRIMARY KEY ,\"WALLET_ID\" INTEGER,\"CN_WALLET_ID\" TEXT,\"CN_USER_ID\" TEXT,\"STATUS\" INTEGER,\"PHONE_NUMBER_HASH\" TEXT,\"PHONE_NUMBER\" TEXT,\"CN_PHONE_NUMBER_HASH_CACHED\" TEXT,\"CN_PHONE_NUMBER_CACHED\" TEXT,\"CN_USER_ID_CACHED\" TEXT,\"VERIFICATION_TTL\" INTEGER NOT NULL )");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"TRANSACTION_NOTIFICATION\" (\"_id\" INTEGER PRIMARY KEY ,\"DROPBIT_ME_HANDLE\" TEXT,\"AVATAR\" TEXT,\"DISPLAY_NAME\" TEXT,\"MEMO\" TEXT,\"IS_SHARED\" INTEGER NOT NULL ,\"AMOUNT\" INTEGER NOT NULL ,\"AMOUNT_CURRENCY\" TEXT,\"TXID\" TEXT,\"PHONE_NUMBER\" TEXT)");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"INTERNAL_NOTIFICATION\" (\"PRIORITY\" INTEGER,\"_id\" INTEGER PRIMARY KEY AUTOINCREMENT UNIQUE ,\"MESSAGE\" TEXT,\"HAS_BEEN_SEEN\" INTEGER NOT NULL ,\"MESSAGE_LEVEL\" INTEGER,\"SERVER_UUID\" TEXT,\"CLICK_ACTION\" TEXT,\"WALLET_ID\" INTEGER)");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"WALLET\" (\"_id\" INTEGER PRIMARY KEY AUTOINCREMENT ,\"HD_INDEX\" INTEGER NOT NULL ,\"USER_ID\" INTEGER,\"LAST_SYNC\" INTEGER NOT NULL ,\"INTERNAL_INDEX\" INTEGER NOT NULL ,\"EXTERNAL_INDEX\" INTEGER NOT NULL ,\"BALANCE\" INTEGER NOT NULL ,\"SPENDABLE_BALANCE\" INTEGER NOT NULL ,\"BLOCK_TIP\" INTEGER NOT NULL ,\"LAST_USDPRICE\" INTEGER NOT NULL ,\"LAST_FEE\" TEXT)");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"TRANSACTION_SUMMARY\" (\"_id\" INTEGER PRIMARY KEY ,\"TXID\" TEXT UNIQUE ,\"TRANSACTIONS_INVITES_SUMMARY_ID\" INTEGER,\"SOUGHT_NOTIFICATION\" INTEGER NOT NULL ,\"WALLET_ID\" INTEGER NOT NULL ,\"FEE\" INTEGER NOT NULL ,\"TX_TIME\" INTEGER NOT NULL ,\"NUM_CONFIRMATIONS\" INTEGER NOT NULL ,\"BLOCKHASH\" TEXT,\"TO_NAME\" TEXT,\"TO_PHONE_NUMBER\" TEXT,\"NUM_INPUTS\" INTEGER NOT NULL ,\"NUM_OUTPUTS\" INTEGER NOT NULL ,\"BLOCKHEIGHT\" INTEGER NOT NULL ,\"HISTORIC_PRICE\" INTEGER NOT NULL ,\"TRANSACTION_NOTIFICATION_ID\" INTEGER,\"MEM_POOL_STATE\" INTEGER)");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"TARGET_STAT\" (\"_id\" INTEGER PRIMARY KEY ,\"ADDRESS_ID\" INTEGER,\"TSID\" INTEGER,\"WALLET_ID\" INTEGER,\"ADDR\" TEXT,\"POSITION\" INTEGER NOT NULL ,\"VALUE\" INTEGER NOT NULL ,\"TX_TIME\" INTEGER NOT NULL ,\"FUNDING_ID\" INTEGER,\"STATE\" INTEGER)");
        db.execSQL("CREATE TABLE IF NOT EXISTS \"TRANSACTIONS_INVITES_SUMMARY\" (\"_id\" INTEGER PRIMARY KEY AUTOINCREMENT ,\"TRANSACTION_SUMMARY_ID\" INTEGER,\"INVITE_SUMMARY_ID\" INTEGER,\"INVITE_TIME\" INTEGER NOT NULL ,\"BTC_TX_TIME\" INTEGER NOT NULL ,\"TRANSACTION_TX_ID\" TEXT,\"INVITE_TX_ID\" TEXT)");
    }
}
